package furhatos.app.furhatlab.roast.utils

import furhatos.app.furhatlab.llm.ChatCompletionModel
import furhatos.app.furhatlab.llm.Message
import furhatos.app.furhatlab.llm.Prompt
import furhatos.app.furhatlab.llm.SystemMessage
import furhatos.app.furhatlab.llm.VisionUserMessage
import furhatos.app.furhatlab.roast.model
import furhatos.app.furhatlab.roast.userInfo
import furhatos.flow.kotlin.FlowControlRunner
import furhatos.flow.kotlin.furhat.camerafeed.getSnapShot
import furhatos.records.User
import kotlinx.coroutines.runBlocking
import java.awt.image.BufferedImage
import java.io.ByteArrayOutputStream
import java.util.Base64
import javax.imageio.ImageIO

fun getUserAppearance(runner: FlowControlRunner, user: User){
    println("[USER_APPEARANCE] User appearance info update process started")
    val snapshotInfo = user.getSnapShot()
    val bufferedImage = snapshotInfo?.first
    
    if (bufferedImage == null) {
        println("[USER_APPEARANCE] Couldn't capture image from camera. Using generic appearance description.")
        userInfo.appearance = null
        return
        // "data:image/png;base64,"
    }
    
    val dataUrlImage = bufferedImageToDataUrl(bufferedImage)
    val description = describeImageWithModel(model, dataUrlImage)
    userInfo.appearance = description

    println("""
        [USER_APPEARANCE] User appearance got updated with the following:
        $description
    """.trimIndent())
}

fun bufferedImageToDataUrl(
    image: BufferedImage,
    format: String = "png"
): String {
    val baos = ByteArrayOutputStream()

    val ok = ImageIO.write(image, format, baos)
    require(ok) { "ImageIO could not write image in format '$format'" }

    val bytes = baos.toByteArray()
    val base64 = Base64.getEncoder().encodeToString(bytes)

    return "data:image/$format;base64,$base64"
}

fun describeImageWithModel(
    model: ChatCompletionModel,
    dataUrlImage: String
): String {
    val systemPrompt = """
        You are a helpful visual assistant.
        Describe the appearance of the person present in the image accurately and concisely.
        Only use information visible in the image.
        Keep the description brief (1-2 sentences).
        
        IMPORTANT RULES:
        • Do not include any inappropriate content
        • Focus only on visible physical features
        • Be respectful and neutral in tone
        • Return ONLY the description, nothing else
    """.trimIndent()

    val messages = listOf<Message>(
        SystemMessage(systemPrompt),
        VisionUserMessage(
            text = "Describe this person's appearance briefly.",
            imageUrl = dataUrlImage
        )
    )

    return try {
        val completion = runBlocking {
            model.request(Prompt(messages))
        }
        completion.text?.trim() ?: "Appearance could not be determined from image."
    } catch (e: Exception) {
        println("[USER_APPEARANCE] Error describing image: ${e.message}")
        "Unable to process image"
    }
}
